mapper.select:
select oi.*, DATE_FORMAT(oi.create_date, '%Y') AS year, DATE_FORMAT(oi.create_date, '%c') AS month from order_info oi where 1 = 1
AND DATE_FORMAT(oi.create_date, '%Y') = #{year}
AND DATE_FORMAT(oi.create_date, '%c') = #{month}

select rt.id, rt.type_name, count(oi.id) as count from room_type rt left join room_info ri on (ri.`type` = rt.id) left join order_info oi on (oi.room_code = ri.code
AND DATE_FORMAT(oi.create_date, '%Y') = #{year}
AND DATE_FORMAT(oi.create_date, '%c') = #{month}
) where 1 = 1 group by rt.id

select rt.id, rt.type_name, IFNULL(SUM(oi.total_price), 0) AS count from room_type rt left join room_info ri on (ri.`type` = rt.id) left join order_info oi on (oi.room_code = ri.code
AND DATE_FORMAT(oi.create_date, '%Y') = #{year}
AND DATE_FORMAT(oi.create_date, '%c') = #{month}
) where 1 = 1 group by rt.id

select tempDay.date, IFNULL(SUM(oi.total_price), 0) AS price from ( select date from ( select DATE_FORMAT(DATE_SUB(last_day(#{checkDate}), interval xc-1 day), '%Y-%m-%d') as date from ( select @xi := @xi + 1 as xc from ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc1, ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc2, ( select @xi := 0) xc0 ) xcxc) x0 where x0.date >= ( select date_add(#{checkDate}, interval-day(#{checkDate})+ 1 day))) as tempDay left join order_info oi on (DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( tempDay.date, '%Y-%m-%d')) left join room_info ri on (ri.code = oi.room_code
AND DATE_FORMAT(oi.create_date, '%Y') = #{year}
AND DATE_FORMAT(oi.create_date, '%c') = #{month}
AND ri.type = #{typeId}
) where 1 = 1 group by tempDay.date

select tempDay.date, count(oi.id) as count from ( select date from ( select DATE_FORMAT(DATE_SUB(last_day(#{checkDate}), interval xc-1 day), '%Y-%m-%d') as date from ( select @xi := @xi + 1 as xc from ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc1, ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc2, ( select @xi := 0) xc0 ) xcxc) x0 where x0.date >= ( select date_add(#{checkDate}, interval-day(#{checkDate})+ 1 day))) as tempDay left join order_info oi on (DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( tempDay.date, '%Y-%m-%d')) left join room_info ri on (ri.code = oi.room_code
AND DATE_FORMAT(oi.create_date, '%Y') = #{year}
AND DATE_FORMAT(oi.create_date, '%c') = #{month}
AND ri.type = #{typeId}
) where 1 = 1 group by tempDay.date
